var DuplexBufferStream = require('../lib/duplexbufferstream');
var fs = require('fs');

var input = fs.createReadStream(process.argv[2] || process.argv[1]);
var duplexbufferstream = new DuplexBufferStream();
var output = process.argv[3] ? fs.createWriteStream(process.argv[3]) : process.stdout;

input.pipe(duplexbufferstream).pipe(output);
